    }
    return(
        <DataContext.Provider value={{
            cart:cart,
            user: user,
            addProductToCart: addProductToCart,
            resetCart:resetCart,
        }}>
            {props.children}
        </DataContext.Provider>
    );
}

export default GlobalProvider;
