import Product from '../components/product';
import './styles/catalog.css';

const categories=['fruit1', 'fruit2', 'fruit3','fruit4', 'fruit5'];



   
const data = [
        {
            title: "Watermelon",
            price: 12.33,
            category: "fruit4",
            image: "watermelon.jpg",
            _id: "1",
            width: 300,
            height: 300
        },
        {
            title: "Banana",
            price: 17.33,
            category: "fruit5",
            image: "banana.jpg",
            _id: "2",
            width: 300,
            height: 300
        },
        {
            title: "Strawberries",
            price: 11.33,
            category: "fruit2",
            image: "strawberry.jpg",
            _id: "3",
            width: 300,
            height: 300
        },
        {
            title: "Oranges",
            price: 16.33,
            category: "fruit3",
            image: "oranges.jpg",
            _id: "4",
            width: 300,
            height: 300
        },
        {
            title: "Pineapple",
            price: 18.33,
            category: "fruit1",
            image: "pineapple.jpg",
            _id: "5",
            width: 300,
            height: 300
        }
    ];
    

function Catalog(){
return(
    <div className="catalog">
        <h1>Check our catalog</h1>
        
        <div className='filters'>
            {categories.map(cat => <button className='btn btn-primary'>{cat}</button>)}
        </div>
        {data.map(prod=> <Product info={prod} />)}
    </div>
);
}

export default Catalog;
